@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

#page {
  font-family: system-ui, sans-serif;
  color: var(--color);
  line-height: 1.6; 
  font-size: 1rem;
  background: var(--background);
}

.section-container {
  max-width: var(--max-width, 1000px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.heading { /* Defined in Site CSS */
  text-align: center;
  margin-bottom: 1rem;
}

ol {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(20rem, 1fr));
  place-items: flex-start;
  gap: 2rem;

  li {
    display: flex;
    gap: 1rem;
    border: 1px solid var(--border-color, #eee);
    border-radius: var(--border-radius);
    padding: 2rem;
  }

  .number {
    color: var(--color-accent, rebeccapurple);
    font-size: 1.25rem;
    font-weight: bold;
  }

  .body {
    display: grid;
    gap: 0.5rem;

    .title {
      font-weight: bold;
      font-size: 1.25rem;
    }
  }
}

.button { /* Defined in Site CSS */
  display: inline-flex;
  align-self: flex-end;
  gap: 0.5rem;

  :global(svg) {
    transition: transform 0.2s;
  }

  &:hover :global(svg) {
    transform: translateX(3px);
  }
}

